pragma solidity ^0.4.19;

import './Hangman.sol';

contract LiveGame is Hangman {

  //event GameStarted(string uniqGameString, string hangman, string challenger, uint8 maxTries, uint8 wager, string word);
  event GameStarted(string challenger, uint gameId, string word);

  struct ActiveGame {
    string word;
    uint8 wager;
    uint8 maxTries;
    uint id;
    string uniqGameString;
    string hangman;
    string challenger;
    uint8 tries;
    string[] hits;
    string[] misses;
  }

  mapping (uint => address) gameToChallenger;

  function findPendingGame (string userWord) internal view returns(string) {
    uint oldId = _generateRandomId(userWord);
    string memory gameWord = games[oldId].word;
    return gameWord;
  }

  function startLiveGame(string challenger, uint gameId) public {
    string memory gameWord = idToWord[gameId];
    GameStarted(challenger, gameId, gameWord);
  }

  /* function startLiveGame(uint8 wager, uint8 maxTries, string uniqGameString, string challenger, uint gameId, string hangman) public {
    string memory word = games[gameId].word;
    uint randId = _generateRandomId(uniqGameString);
    uint8 tries = 0;
    string[] memory hits;
    string[] memory misses;
    gameToChallenger[randId] = msg.sender;
    activeGamesMap[randId] = ActiveGame(word, wager, maxTries, randId, uniqGameString, hangman, challenger, tries, hits, misses);
    GameStarted(uniqGameString, hangman, challenger, maxTries, wager, word);
    GameStarted(word);
  } */

  function _generateRandomId(string word) private pure returns (uint) {
    uint rand = uint(keccak256(word));
    return rand;
  }
}
